import React, { useState, useEffect } from "react";
import { CloseCircleOutlined } from "@ant-design/icons";
import { useParams } from "react-router-dom";
import {
  Row,
  Button,
  Col,
  message,
  Image,
  Input,
  Upload,
  Form,
  Modal,
} from "antd";
import axios from "axios";
import "./style.css";

const MediaInput = (props) => {
  const { onChange, onRemove, value, id } = props;
  const [modalVisble, setModalVisible] = useState(false);
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [imageId, setImageId] = useState();

  useEffect(() => {
    if (value) {
      getMedia(value);
    }
  }, [value]);

  const getMedia = (id) => {
    return axios
      .get(
        `https://pumba-api.onalabs.org/items/media/${id}?access_token=${process.env.REACT_APP_ACCESS_TOKEN}`
      )
      .then((res) => {
        setImageId(res.data.data.file);
      })
      .catch((err) => console.log(err));
  };

  const uploadProps = {
    onRemove: (file) => {
      const index = fileList.indexOf(file);
      const newFileList = fileList.slice();
      newFileList.splice(index, 1);
      setFileList(newFileList);
    },
    beforeUpload: (file) => {
      setFileList([...fileList, file]);

      return false;
    },
    fileList,
  };

  const handleUpload = () => {
    const formData = new FormData();
    formData.append("title", "My File");
    /*
    fileList.forEach((file) => {
      formData.append('files[]', file);
    });

    */
    formData.append("file", fileList[0]);

    setUploading(true);
    // You can use any AJAX library you like
    axios
      .post(
        `https://pumba-api.onalabs.org/files?access_token=${process.env.REACT_APP_ACCESS_TOKEN}`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      )
      .then((res) => {
        console.log(res.data.data);
        setFileList([]);
        if (res.data.data.id) {
          createMedia(res.data.data.id);
        }
        if (res.data.data.length > 0) {
          res.data.data.forEach((item) => createMedia(item.id));
        }
        message.success("upload successfully.");
      })
      .catch(() => {
        message.error("upload failed.");
      })
      .finally(() => {
        setUploading(false);
      });
  };

  const createMedia = (id) => {
    return axios
      .post(
        `https://pumba-api.onalabs.org/items/media?access_token=${process.env.REACT_APP_ACCESS_TOKEN}`,
        {
          status: "published",
          file: id,
        }
      )
      .then((res) => {
        onChange(res.data.data.uuid);
        console.log("media created...");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="field-media-row">
      <a
        href="#remove"
        className="field-media-row-delete"
        onClick={(e) => {
          e.preventDefault();
          onRemove();
        }}
      >
        <CloseCircleOutlined />
      </a>
      <Upload {...uploadProps}>
        {!value && <Button>Select media</Button>}
      </Upload>
      {value && (
        <Image
          width="100%"
          src={`https://pumba-api.onalabs.org/assets/${imageId}`}
          fallback="data:image/png;base64,"
        />
      )}
      {fileList.length > 0 && (
        <Button
          type="primary"
          onClick={handleUpload}
          disabled={fileList.length === 0}
          loading={uploading}
          style={{ marginTop: 16 }}
        >
          {uploading ? "Uploading" : "Upload"}
        </Button>
      )}
    </div>
  );
};

export { MediaInput };
